import React, { useState, useEffect} from 'react'
import tmdbApi, {category, movieType} from '../../api/tmdbApi'
import apiConfig from '../../api/apiConfig'
import Button,{OutlineButton} from '../button/Button'
import './mainslide.scss'
import SwiperCore, {Autoplay} from 'swiper'
import {Swiper, SwiperSlide} from 'swiper/react'
import { useNavigate } from 'react-router-dom';
import 'swiper/scss'
import 'swiper/scss/navigation'
import 'swiper/scss/pagination'


const MainSlide = () => {
    SwiperCore.use([Autoplay]);

    const [movieItems, setMovieItems] = useState([]);

    useEffect(() => {
        const getMovies =  async () => {
            const params = {page: 1}
            try {
                const response =  await tmdbApi.getMoviesList(movieType.popular, {params});
                setMovieItems(response.results.slice(1,4));
                console.log(response);

            } catch {
                console.log('error')
            }
        }
        getMovies();
    }, [])


    return (
        <div className="main-slide">
            <Swiper
                modules={[Autoplay]}
                grabCursor={true}
                spaceBetween={0}
                slidesPerView={1}
                autoplay={{delay: 3000,disableOnInteraction: false,}}
            >
                {
                movieItems.map((item,i) => (
                    <SwiperSlide key={i}>
                        {({isActive}) => (
                            // <h2 onClick={() => console.log(item)}>hihihihi</h2>
                            // <img src={apiConfig.originImage(item.backdrop_path)} alt="title" />
                            <MainSlideItem
                                item={item}
                                className={`${isActive? 'active' : ''}`}     
                            />
                        )}
                    </SwiperSlide>
                ))
                } 

             </Swiper>
         </div>
    )
}

const MainSlideItem = props => {
    let history = useNavigate();

    const item =  props.item;

    const background = apiConfig.originImage(item.backdrop_path ? item.backdrop_path : item.poster_path);
    // console.log(background)

    const watchNowActive = () => {
        // history('/movie' + item.id);
        console.log("dadad")
    }
    return (
        <div 
            className={`main-slide__item ${props.className}`}
            style={{backgroundImage: `url(${background})`}}
        >
            <div className="main-slide__item__content container">
                <div className="main-slide__item__content__info">
                    <h2 className="title">{item.title}</h2>
                    <div className="overview">{item.overview}</div>
                    <div className="btns">
                        <Button onClick={watchNowActive}>
                            Watch now
                        </Button>
                        <OutlineButton onClick={() => console.log("trailer")}>
                            Trailer
                        </OutlineButton>
                    </div>
                </div>
                <div  className="main-slide__item__content__poster">
                    <img src={apiConfig.originImage(item.poster_path)} alt={item.title} />
                </div>
            </div>

        </div>
    )
}


export default MainSlide
