import { fromJSON } from "postcss"
import {SET_TODO_INPUT} from './consts'

const initState ={
    todos: [],
    todoInput: '',
}

function reducer(state,action){
    switch(action.type) {
        case SET_TODO_INPUT:
            return {
            ...state,
            todoInput: action.payload
        }
        break
        default:
           throw new Error('invalid action')

    }
}
export {initState}
export default reducer
